package com.csejnu.diary.Activity;import android.os.Bundle;import android.view.View;import android.widget.AdapterView;import android.widget.ArrayAdapter;import android.widget.Button;import android.widget.EditText;import android.widget.Spinner;import android.widget.Toast;import androidx.appcompat.app.AppCompatActivity;import com.csejnu.diary.Database.Database;import com.csejnu.diary.Model.Faculty;import com.csejnu.diary.R;import java.util.ArrayList;import java.util.Arrays;import java.util.Calendar;import java.util.List;public class AddEntryActivity extends AppCompatActivity {    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_add_entry);        List<String> onushodList = Arrays.asList(getResources().getStringArray(R.array.onushodList));        List<String> biggan_onushod = Arrays.asList(getResources().getStringArray(R.array.biggan_onushod));        List<String> samajil_biggan_onushod = Arrays.asList(getResources().getStringArray(R.array.samajil_biggan_onushod));        List<String> kola_onushod = Arrays.asList(getResources().getStringArray(R.array.kola_onushod));        List<String> bussiness_studies_onushod = Arrays.asList(getResources().getStringArray(R.array.bussiness_studies_onushod));        List<String> aiin_onushod = Arrays.asList(getResources().getStringArray(R.array.aiin_onushod));        List<String> life_and_earth_science_onushod = Arrays.asList(getResources().getStringArray(R.array.life_and_earth_science_onushod));        List<String> doptor_somuh = Arrays.asList(getResources().getStringArray(R.array.doptor_somuh));        final Button submit = findViewById(R.id.submit);        EditText name = findViewById(R.id.name);        final List<String>[] selected_dept = new List[]{new ArrayList<>()};        Spinner spinner_dept = findViewById(R.id.spinner_dept);        Spinner spinner_title = findViewById(R.id.spinner_title);        ArrayAdapter spinnerArrayAdapter_title = new ArrayAdapter(this,                android.R.layout.simple_spinner_dropdown_item, getResources().getStringArray(R.array.titles));        spinner_title.setAdapter(spinnerArrayAdapter_title);        Spinner spinner_onushod = findViewById(R.id.spinner_onushod);        ArrayAdapter spinnerArrayAdapter_onushod = new ArrayAdapter(this,                android.R.layout.simple_spinner_dropdown_item, getResources().getStringArray(R.array.onushodList));        spinner_onushod.setAdapter(spinnerArrayAdapter_onushod);        spinner_onushod.setOnItemSelectedListener(new AdapterView.OnItemSelectedListener() {            public void onItemSelected(AdapterView<?> parent, View view, int position, long id) {                switch (position) {                    case 0:                        Toast.makeText(AddEntryActivity.this, "Error, You must have to select any.", Toast.LENGTH_SHORT).show();                        ;                    case 1:                        selected_dept[0] = biggan_onushod;                    case 2:                        selected_dept[0] = samajil_biggan_onushod;                    case 3:                        selected_dept[0] = kola_onushod;                    case 4:                        selected_dept[0] = bussiness_studies_onushod;                    case 5:                        selected_dept[0] = aiin_onushod;                    case 6:                        selected_dept[0] = life_and_earth_science_onushod;                    case 7:                        selected_dept[0] = doptor_somuh;                }                ArrayAdapter spinnerArrayAdapter_dept = new ArrayAdapter(getApplicationContext(), android.R.layout.simple_spinner_dropdown_item, selected_dept);                spinner_dept.setAdapter(spinnerArrayAdapter_dept);            }            public void onNothingSelected(AdapterView<?> parent) {            }        });        submit.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View view) {                String onusud_name = spinner_onushod.getSelectedItem().toString();                String dept_name = spinner_dept.getSelectedItem().toString();                String title_name = spinner_title.getSelectedItem().toString();                String name = ((EditText) findViewById(R.id.address_name)).getText().toString();                String number_home = ((EditText) findViewById(R.id.numeber_home)).getText().toString();                String number_office = ((EditText) findViewById(R.id.numeber_office)).getText().toString();                String email = ((EditText) findViewById(R.id.address_name)).getText().toString();                String fax = ((EditText) findViewById(R.id.address_fax)).getText().toString();                String random_id = String.valueOf(Calendar.getInstance().getTimeInMillis());                Faculty faculty = new Faculty(random_id, name, onusud_name, title_name, dept_name, number_home, number_office, email, fax);                Database db = new Database(getApplicationContext());                db.insertFaculty(faculty);            }        });    }}