package com.csejnu.diary.Fragments;import android.annotation.SuppressLint;import android.os.Bundle;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.webkit.WebView;import androidx.annotation.NonNull;import androidx.annotation.Nullable;import androidx.fragment.app.Fragment;import com.csejnu.diary.R;public class FragmentNotices extends Fragment {    WebView notices;    @Override    public void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);    }    @Override    public View onCreateView(LayoutInflater inflater, ViewGroup container,                             Bundle savedInstanceState) {        return inflater.inflate(R.layout.frag_notices, container, false);    }    @SuppressLint("SetJavaScriptEnabled")    @Override    public void onViewCreated(@NonNull View view, @Nullable Bundle savedInstanceState) {        super.onViewCreated(view, savedInstanceState);        try {            notices = view.findViewById(R.id.notices);            notices.loadUrl("https://jnu.ac.bd/portal/noticeview/1");            notices.getSettings().setSupportZoom(true);            notices.getSettings().setJavaScriptEnabled(true);            notices.getSettings().setJavaScriptCanOpenWindowsAutomatically(true);        } catch (NullPointerException jhj) {        }    }}