package com.csejnu.diary.Model;import java.util.ArrayList;import java.util.List;public class Department {    String departmentName;    List<Faculty> facultyMembers = new ArrayList<>();    public Department(String departmentName, List<Faculty> facultyMembers) {        this.departmentName = departmentName;        this.facultyMembers = facultyMembers;    }    public Department() {    }    public String getDepartmentName() {        return departmentName;    }    public void setDepartmentName(String departmentName) {        this.departmentName = departmentName;    }    public List<Faculty> getFacultyMembers() {        return facultyMembers;    }    public List<Faculty> getFacultyByTitle(String title) {        List<Faculty> data = new ArrayList<>();        for (int i=0;i<facultyMembers.size(); i++){            if(facultyMembers.get(i).getTitle().equals(title)) {data.add(facultyMembers.get(i));}        }        return data;    }    public void setFacultyMembers(List<Faculty> facultyMembers) {        this.facultyMembers = facultyMembers;    }}